import axios from 'axios';

const Api = axios.create({
  baseURL: 'http://localhost:5000',
  withCredentials: true,
  headers: {
    'Content-Type': 'application/json', // assuming most requests will be JSON
  },
});

// configuration for axios with token
const getConfig = () => ({
  headers: {
    Authorization: `Bearer ${localStorage.getItem('token')}`,
  },
});

// Logging the token for debugging
console.log('Token:', getConfig().headers.Authorization);

// Creating test api
export const testApi = () => Api.get('/test');

// Auth API for user
// Creating register API
export const registerApi = data => Api.post('/api/users/register', data);

// Creating login API
export const loginApi = data => Api.post('/api/users/login', data);
