import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { registerApi } from '../apis/Api';
import '../css/regstyle.css';

const Register = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [userName, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const navigate = useNavigate();

  const changeFullName = e => setFullName(e.target.value);
  const changeUserName = e => setUserName(e.target.value);
  const changeEmail = e => setEmail(e.target.value);
  const changePassword = e => setPassword(e.target.value);
  const changeConfirmPassword = e => setConfirmPassword(e.target.value);

  const validateFullName = name => /^[A-Za-z\s]+$/.test(name);
  const validateUsername = username => /^[A-Za-z0-9]+$/.test(username);
  const validateEmail = email => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  const validatePassword = password => password.length >= 8 && /\d/.test(password) && /[A-Za-z]/.test(password);

  const handleSubmit = e => {
    e.preventDefault();

    if (!validateFullName(fullname)) {
      toast.error("Full name must contain only letters and spaces");
      return;
    }

    if (!validateUsername(userName)) {
      toast.error("Username must be alphanumeric and contain no spaces");
      return;
    }

    if (!validateEmail(email)) {
      toast.error("Invalid email format");
      return;
    }

    if (!validatePassword(password)) {
      toast.error("Password must be at least 8 characters long and contain both letters and numbers");
      return;
    }

    if (password !== confirmPassword) {
      toast.error("Passwords do not match");
      return;
    }

    const data = {
      fullname: fullname,
      userName: userName,
      email: email,
      password: password,
    };

    registerApi(data)
      .then(res => {
        if (res.data.success === true) {
          toast.success(res.data.message);
          navigate('/login');
        } else {
          toast.error(res.data.message);
        }
      })
      .catch(err => {
        console.log(err);
        toast.error('Internal Server Error');
      });
  };

  return (
    <div className="register-background">
      <div className="form-container">
        <h1>Register Now</h1>
        <p>Register now to start your journey</p>

        <div className="input-group">
          <label htmlFor="fullname">Full Name</label>
          <input
            type="text"
            id="fullname"
            name="fullname"
            value={fullname}
            onChange={changeFullName}
            placeholder="Full Name"
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            name="username"
            value={userName.toLowerCase().replace(/ /g, "")}
            onChange={changeUserName}
            placeholder="Username"
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="emaill">Email</label>
          <input
            type="text"
            id="emaill"
            name="email"
            value={email.toLowerCase().replace(/ /g, "")}
            onChange={changeEmail}
            placeholder="Email"
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={changePassword}
            placeholder="Enter your password"
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="confirm-password">Confirm Password</label>
          <input
            type="password"
            id="confirm-password"
            name="confirmPassword"
            value={confirmPassword}
            onChange={changeConfirmPassword}
            placeholder="Confirm Password"
            required
          />
        </div>
        <button type="submit" onClick={handleSubmit}>Sign Up</button>
        <p>Already have an account? <Link to="/login">Sign In</Link></p>
      </div>
    </div>
  );
};

export default Register;
